const authHeader = msg.req.headers.authorization || msg.req.headers.Authorization;

node.log(`ðŸ›’ CART ACCESS ATTEMPT: ${msg.req.method} ${msg.req.url}`);

// Check if Authorization header exists
if (!authHeader) {
    node.warn(`âŒ No Authorization header provided`);
    msg.statusCode = 401;
    msg.payload = { 
        success: false,
        message: 'Access token missing',
        error: 'NO_AUTH_HEADER'
    };
    return [null, msg]; // Send to error response
}

// Check if it's a Bearer token
if (!authHeader.startsWith('Bearer ')) {
    node.warn(`âŒ Invalid Authorization header format`);
    msg.statusCode = 401;
    msg.payload = { 
        success: false,
        message: 'Invalid authorization format. Use Bearer token',
        error: 'INVALID_AUTH_FORMAT'
    };
    return [null, msg];
}

const token = authHeader.split(' ')[1];
if (!token) {
    node.warn(`âŒ No token provided`);
    msg.statusCode = 401;
    msg.payload = { 
        success: false,
        message: 'Access token missing',
        error: 'NO_TOKEN'
    };
    return [null, msg];
}

node.log(`âœ… CART AUTH TOKEN VALIDATION PASSED`);

// Forward the authorization header to the cart service
msg.headers = {
    'Authorization': authHeader
};

return [msg, null]; // Send to success flow

const isSuccess = msg.statusCode >= 200 && msg.statusCode < 300;

if (isSuccess && msg.payload) {
    const cartData = msg.payload;

    node.log(`âœ… SUCCESS: Cart data retrieved - ${cartData.Products ? cartData.Products.length : 0} products, total: ${cartData.total || 0}`);

    // Create structured response
    msg.payload = {
        success: true,
        message: 'Cart retrieved successfully',
        cart: {
            products: cartData.Products || [],
            total: cartData.total || 0,
            itemCount: cartData.Products ? cartData.Products.length : 0
        },
        timestamp: new Date().toISOString()
    };

    // Update cart statistics
    let cartStats = global.get('cartServiceStats') || {
        getRequests: 0,
        addRequests: 0,
        deleteRequests: 0,
        checkoutRequests: 0,
        errors: 0,
        startTime: new Date().toISOString()
    };
    cartStats.getRequests++;
    global.set('cartServiceStats', cartStats);

    msg.statusCode = 200;
} else {
    node.warn(`âŒ FAILED: Cart access failed - Status: ${msg.statusCode}`);

    if (msg.statusCode === 401) {
        msg.payload = {
            success: false,
            message: 'User is not authorized',
            error: 'INVALID_TOKEN'
        };
    } else {
        msg.payload = {
            success: false,
            message: 'Failed to retrieve cart',
            error: msg.payload.error || 'Unknown error'
        };
    }
}

return msg;

